#!/bin/bash

conda activate architect_env

wait;

EXPNAME="r_1" CUDA_VISIBLE_DEVICES=2, XLA_PYTHON_CLIENT_MEM_FRACTION=0.1 python architect/experiments/highway/predict_and_mitigate.py --model_path results/highway/initial_policy.eqx --seed 0 --num_rounds 10 --num_steps_per_round 1 --num_chains 5 --ep_mcmc_step_size 1e-1 --dp_mcmc_step_size 1e-1 --L 10 --grad_clip 2 --no-repair --predict --savename response_hw &
EXPNAME="r_0" CUDA_VISIBLE_DEVICES=2, XLA_PYTHON_CLIENT_MEM_FRACTION=0.1 python architect/experiments/highway/predict_and_mitigate.py --model_path results/highway/initial_policy.eqx --seed 0 --num_rounds 10 --num_steps_per_round 1 --num_chains 5 --ep_mcmc_step_size 1e-1 --dp_mcmc_step_size 1e-1 --L 10 --grad_clip 2 --zero_order_gradients --no-repair --predict --savename response_hw &
EXPNAME="rmh" CUDA_VISIBLE_DEVICES=2, XLA_PYTHON_CLIENT_MEM_FRACTION=0.1 python architect/experiments/highway/predict_and_mitigate.py --model_path results/highway/initial_policy.eqx --seed 0 --num_rounds 10 --num_steps_per_round 1 --num_chains 5 --ep_mcmc_step_size 1e-1 --dp_mcmc_step_size 1e-1 --disable_gradients --L 10 --no-temper --no-repair --predict --savename response_hw &
